mapper.select:
select count(*) from user_mst as um left outer join department_mst as dm on um.department_number = dm.department_number where 1 = 1
and (um.username like concat('%' #{searchValue}, '%') or um.name like concat('%', #{searchValue}, '%'))
and category = #{category}
limit #{index}, #{count}

select dm.category, um.username, um.name, um.password, um.phone, um.birth_date as birthDate, um.email from user_mst as um left outer join department_mst as dm on um.department_number = dm.department_number where 1 = 1
and(um.username like concat('%' #{searchValue}, '%') or um.name like concat('%', #{searchValue}, '%'))
and category = #{category}
limit #{index}, #{count}

mapper.update:
update student_mst
set
    grade = #{grade}
where
    user_id = #{userId}

mapper.select:
select category, classification, subject_code as subjectCode, subject_name as subjectName, credit, professor_name as professorName, building, lecture_time as lectureTime from subject_mst where 1=1
and ( subject_name like CONCAT('%', #{searchValue}, '%') or professor_name like CONCAT('%', #{searchValue}, '%') or classification like CONCAT('%', #{searchValue}, '%') )
and category = #{category}
order by subjectCode
limit #{index}, #{count}

select count(*) from subject_mst where 1 = 1
and ( subject_name like CONCAT('%', #{searchValue}, '%') or professor_name like CONCAT('%', #{searchValue}, '%') or classification like CONCAT('%', #{searchValue}, '%') )
and category = #{category}
