mapper.select:
select count(bm.board_id) as boardId, bm.board_subject as boardSubject, um.name as name, um.user_id as userId from board_mst bm inner join user_mst um on (bm.user_id = um.user_id) where 1=1
and ( bm.board_id like CONCAT('%', #{searchValue}, '%') or bm.board_subject like CONCAT('%', #{searchValue}, '%') or um.name like CONCAT('%', #{searchValue}, '%') )
limit #{index}, #{count}

select bm.board_id as boardId, bm.board_subject as boardSubject, um.name as name, bm.board_visit as boardVisit, bm.board_content as boardContent, bm.board_upload_name as boardUploadName, bm.board_upload_size as boardUploadSize, bm.board_upload_trans as boardUploadTrans, bm.board_regdate as boardRegDate, bm.board_grp as boardGrp from board_mst bm inner join user_mst um on (bm.user_id = um.user_id) inner join board_category bc on (bm.board_grp = bc.board_grp) where bm.board_grp = #{boardGrp}
and ( bm.board_id like CONCAT('%', #{searchValue}, '%') or bm.board_subject like CONCAT('%', #{searchValue}, '%') or um.name like CONCAT('%', #{searchValue}, '%') )
order by bm.board_id desc
limit #{index}, #{count}

select count(bm.board_id) as boardId, bm.board_subject as boardSubject, um.name as name, um.user_id as userId from board_mst bm inner join user_mst um on (bm.user_id = um.user_id) where bm.board_grp = #{boardGrp}
and ( bm.board_id like CONCAT('%', #{searchValue}, '%') or bm.board_subject like CONCAT('%', #{searchValue}, '%') or um.name like CONCAT('%', #{searchValue}, '%') )
limit #{index}, #{count}
